import React, { useEffect, useState } from 'react'

export const App = () => {
  const [users, setUsers] = useState([]);
  const [selectedAddress, setSelectedAddress] = useState(null);
  const [selectedGeoLocation, setSelectedGeoLocation] = useState(null);
  const [selectedCompany, setSelectedCompany] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [showGeoLocationModal, setShowGeoLocationModal] = useState(false);
  const [showCompanyModal, setShowCompanyModal] = useState(false);

  const imagenesQuemadas=[
    "https://cdn.domestika.org/c_fill,dpr_auto,f_auto,q_auto/v1425034585/content-items/001/228/844/sesion-estudio-barcelona-10-original.jpg?1425034585",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "https://media.licdn.com/dms/image/C4D03AQEcsLU_H-Vzog/profile-displayphoto-shrink_800_800/0/1566673009613?e=2147483647&v=beta&t=k38PM77Jw8DfRVBnKDeBt38phtwT36iOEb7nq7OXJ2k",
    "data:image/jpeg;base64,",
    "https://media.licdn.com/dms/image/C4E03AQES_BgG1Hxlkg/profile-displayphoto-shrink_800_800/0/1551840481337?e=2147483647&v=beta&t=z8R4M3P6O4p1hIKo2AvQ8qILn5yCWbo5feasLgZ4Kxc"
  ]

  const fetchUsers = async () => {
    try {
      const response = await fetch(
        'https://jsonplaceholder.typicode.com/users?classId=da0bbae3-c91b-48a9-bc6a-09da01437b1b&assignmentId=481bc5d0-ca48-4dd8-ae75-388370f858b0&submissionId=b8601e97-e8d8-854c-3a7f-908158d0e471'
      );
      if (response.ok) {
        const data = await response.json();
        setUsers(data);
      }
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };
  const handleShowModal = (address) => {
    setSelectedAddress(address);
    setShowModal(true);
  }

  const handleShowGeoLocationModal = (geo) => {
    setSelectedGeoLocation(geo);
    setShowGeoLocationModal(true);
  };

  const handleShowModalCompany = (company) => {
    setSelectedCompany(company);
    setShowCompanyModal(true);
  };

  useEffect(() => {
    fetchUsers()
  }, [])


  return (
    <>
    <div className='p-0 m-0' style={{backgroundColor: "#EECB5E"}}>
      <div className="container shadow pt-2 px-5 ps-5 pb-5 mt-" style={{backgroundColor: "white"}}>
      <div className='d-flex align-items-center justify-content-center mt-5'>
      <button className='btn btn-primary' style={{cursor: "default", borderRadius: "15%", fontSize: "12px", paddingLeft: "20px", paddingRight: "20px"}}>Proyect</button>
        </div>
        <div className='d-flex align-items-center justify-content-center mt-2'>
          <h1>Experience Profesional Teams</h1>
        </div>
        <div className='d-flex align-items-center justify-content-center mb-4'>
          <p>Proyecto con fines educativos sobre consumo de APIS</p>
        </div>
        <div className="row">
          {users.map((user, index) => (
            <div key={user.id} className="col-md-6 mb-4">
              <div className="card shadow" style={{ borderRadius: "05%" }}>
                <div className="card-body">
                  <div className="row">
                    <div className="col-md-6">
                      <img src={imagenesQuemadas[index]} className='img-fluid' alt="" style={{ width: "100%" }} /> 
                      {/* EL ARREGLO DE IMAGENES QUEMADAS TIENE 9 INDICES CONTANDO DESDE EL 0, EL CUAL ES EL NUMERO DE USUARIOS DE LA API */}
                      {/* POR LO MISMO SE USA UN ARREGLO PARA QUE AL HACER CADA MAPEO VAYA CAMBIANDO POR MEDIO DEL INDEX */}
                    </div>
                    <div className="col-md-6">
                      <h5 className="card-title text-center mt-5" style={{fontSize: "21px"}}><strong>Name:</strong> {user.name}</h5>
                      <p className="card-text text-center mt-3"><strong>Username: </strong>{user.username}</p>
                      <p className="card-text text-center"><strong>Email: </strong>{user.email}</p>
                      <p className="card-text text-center"><strong>Phone: </strong>{user.phone}</p>
                    </div>
                  </div>
                </div>
                <div className="card-footer text-center">
                  <button className="btn btn-success ms-1 mx-1 mt-1" onClick={() => handleShowModal(user.address)}><strong>Address <i className="fa fa-car mx-2"></i> </strong>
                  </button>
                  <button className='btn btn-primary mt-1 mx-1 ms-1' onClick={() => handleShowGeoLocationModal(user.address.geo)}><strong>GeoLocation <i className="fa fa-send mx-2"></i></strong></button>
                  <button className='btn btn-info mt-1 mx-1 ms-1'
                    style={{ color: "white" }}
                    onClick={() => handleShowModalCompany(user.company)}><strong>Company <i className="fa fa-building mx-2"></i></strong></button>
                </div>
              </div>
            </div>
          ))}
        </div>
        {showModal && (
          <div className="modal fade show" style={{ display: 'block' }}>
            <div className="modal-dialog">
              <div className="modal-content">
                <div className="modal-header d-flex">
                  <h2 className="modal-title mx-auto ms-auto">Address</h2>
                </div>
                <div className="modal-body text-center">
                  <p><strong>Street: </strong>{selectedAddress?.street}</p>
                  <p><strong>Suite: </strong>{selectedAddress?.suite}</p>
                  <p><strong>City: </strong>{selectedAddress?.city}</p>
                  <p><strong>Zip Code: </strong>{selectedAddress?.zipcode}</p>
                </div>
                <div className="modal-footer">
                  <button className="btn btn-danger" onClick={() => setShowModal(false)}>Close <i className="fa fa-trash mx-2"></i></button>
                </div>
              </div>
            </div>
          </div>
        )}
        {showGeoLocationModal && (
          <div className="modal fade show" style={{ display: 'block' }}>
            <div className="modal-dialog">
              <div className="modal-content">
                <div className="modal-header d-flex">
                  <h2 className="modal-title mx-auto ms-auto">GeoLocation</h2>
                </div>
                <div className="modal-body text-center">
                  <p><strong>Latitude: </strong>{selectedGeoLocation?.lat}</p>
                  <p><strong>Longitude: </strong>{selectedGeoLocation?.lng}</p>
                </div>
                <div className="modal-footer">
                  <div className="mx-auto ms-1">
                  <button className='btn btn-success'
                  onClick={()=>{
                    window.open(`https://www.google.com/maps?q=${selectedGeoLocation?.lat},${selectedGeoLocation?.lng}`, '_blank');
                  }}>Location <i className="fa fa-google mx-2"></i></button>
                  </div>
                  <button className="btn btn-danger" onClick={() => setShowGeoLocationModal(false)}>Close <i className="fa fa-trash mx-2"></i></button>
                </div>
              </div>
            </div>
          </div>
        )}
        {showCompanyModal && (
          <div className="modal fade show" style={{ display: 'block' }}>
            <div className="modal-dialog">
              <div className="modal-content">
                <div className="modal-header d-flex">
                  <h2 className="modal-title mx-auto ms-auto">Company</h2>
                </div>
                <div className="modal-body text-center">
                  <p><strong>Name: </strong>{selectedCompany?.name}</p>
                  <p><strong>CatchPhrase: </strong>{selectedCompany?.catchPhrase}</p>
                  <p><strong>BS: </strong>{selectedCompany?.bs}</p>
                </div>
                <div className="modal-footer">
                  <button className="btn btn-danger" onClick={() => setShowCompanyModal(false)}>Close <i className="fa fa-trash mx-2"></i></button>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
      </div>
    </>
  )
}
